import React, { useState } from "react";
import { WriteUser, UpdateUser } from './Firebase';
import './App.css';

const Counter = () => {

    //num useState선언
    const [num, setNum] = useState(0);
    
    // 증가하는 함수
    const plus = () => {
        setNum(num + 1);
        UpdateUser(num + 1); // 현재 수 데이터베이스에 쓰기
    }
    
    // 감소하는 함수
    const minus = () => {
        if(num !== 0){
            setNum(num - 1);
            UpdateUser(num - 1);
        }
    }

    // name, phone useState선언
    const [name, setName] = useState("");
    const [phone, setPhone] = useState("");
    
    // input 상태 추가 함수
    const onChange = (e) => {
        let target = e.target.name;
        let value = e.target.value;

        if (target === "name") {
            setName(value);
        }else if (target === "phone") {
            const regex = /^[0-9\b -]{0,13}$/; //길이는 13까지, 숫자와 -만 허용
            if (regex.test(value) && value.length === 11) {
                setPhone(value.replace(/-/g, '').replace(/(\d{3})(\d{4})(\d{4})/, '$1-$2-$3')); // 자동으로 - 넣기
            }
        }
    }
    
    // submit 함수 
    const handleSubmit = (e, name, phone) => {
        e.preventDefault(); // 새로고침 안 되도록
        WriteUser(name, phone, 0); // 파이어베이스에 저장
    }

    return (
        <div className="App">
            <form onSubmit={handleSubmit}>
                <p>정보 입력</p>
                <p><input type ="text" placeholder="이름을 입력해주세요." name="name"  onChange={onChange}/><br/>
                <input type ="text" placeholder="휴대폰번호를 입력해주세요." name="phone" onChange={onChange}/><br/>
                <button type="submit" className="submit_btn">제출</button></p>
                
                <p>숫자 입력</p>
                <p><h1>{num}</h1>
                <button type="button" className="plus_btn" onClick={plus}>+1</button>
                <button type="button" className="minus_btn" onClick={minus}>-1</button></p>
            </form>
        </div>
    );
}

export default Counter;
