import React, { useState } from "react";
import { UpdateNum } from './Firebase';
import './App.css';

const Counter = () => {

    //num useState선언
    const [num, setNum] = useState(0);
    
    // 증가하는 함수
    const plus = () => {
        setNum(num + 1);
        UpdateNum(num + 1); // 현재 수 데이터베이스에 쓰기
    }
    
    // 감소하는 함수
    const minus = () => {
        if(num !== 0){
            setNum(num - 1);
            UpdateNum(num - 1);
        }
    }

    return (
        <div className="App">    
            {/* <p>숫자 입력</p>
            <p><h1>{num}</h1>
            <button type="button" className="plus_btn" onClick={plus}>+1</button>
            <button type="button" className="minus_btn" onClick={minus}>-1</button></p> */}
        </div>
    );
}

export default Counter;
