import firebase from "firebase";
import "firebase/auth";
import "firebase/firestore";
// import uuid from "react-uuid";
//uuid 활용
//window.sessionStorage.setItem("userId", uuid());

// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
var firebaseConfig = {
  apiKey: "AIzaSyBptMJhUc4gjRfOHNYhybCgfQO5YQLdP3E",
  authDomain: "reactfirebase-30c10.firebaseapp.com",
  databaseURL: "https://reactfirebase-30c10-default-rtdb.firebaseio.com",
  projectId: "reactfirebase-30c10",
  storageBucket: "reactfirebase-30c10.appspot.com",
  messagingSenderId: "440103573360",
  appId: "1:440103573360:web:715853e90fb1563a8a61b5",
  measurementId: "G-S9BB5XF3JP"
};

// Initialize Firebase
firebase.initializeApp(firebaseConfig);


let number = []; // 숫자를 넣어 중복을 확인할 배열

let output = window.localStorage.getItem("ranNum");
if(!output){
  window.localStorage.setItem("ranNum", JSON.stringify(number));
}else{
  number = JSON.parse(output);
}

// userId 중복없이 랜덤숫자 생성 
const random = () => {
  back:
  while(true){
    let ranNum = Math.floor(Math.random()*20+1); //1~10까지 숫자
  
    if(number.indexOf(ranNum) === -1){ // 중복숫자가 없으면
      number.push(ranNum);
      window.localStorage.setItem("ranNum", JSON.stringify(number)); // 숫자배열 로컬스토리지에 저장
      window.sessionStorage.setItem("userId", ranNum); // 랜덤 숫자 세션스토리지 저장
      break;
    }else{ // 있으면 다시 숫자 뽑기
      continue back;
    }
  }
}
random();
let userId = window.sessionStorage.getItem("userId");

// 데이터 쓰기
const CreateUser = (userName, userPhone) => {
  const userListRef = firebase.database().ref('Users');
  const newUserRef = userListRef.child('User' + userId);
  
  newUserRef.set({
    userId: JSON.parse(userId),
    userName: userName,
    userPhone: userPhone
  });
}


// 숫자 버튼 클릭시 업데이트
const UpdateNum = (num) => {
  firebase.database().ref('Users').child('User' + userId ).update({
  count: num
  });
}


// // 데이터 읽기 -- 중복 회원가입 없게
const ReadUser = (userPhone) => { //휴대폰 번호로 판단
    firebase.database().ref().child('Users').orderByChild('userPhone').equalTo(userPhone).on("value", snapshot => {
      if (snapshot.exists()){
        console.log("exists!");
        const phone = snapshot.val();
        console.log(phone);
        return true;
      }else{
        console.log("not exists!");
      }
      return false;
    });
}


// 필요한 곳에서 사용할 수 있도록 내보내기
export {CreateUser, UpdateNum, ReadUser};
