import React from 'react';
import './App.css';
import { firestore } from "./firebase";

function App() {
  return (
    <div className="App">
      <h1 className="number">{ num }</h1>
      <div className="buttons">
        <button type="button" className="plus_btn" onClick={plus}>+1</button>
        <button type="button" className="minus_btn" onClick={minus}>-1</button>
      </div>  
    </div>
  );
}

let num = 0;

function plus(e) {
  num++; 
  console.log(num);
  return num
}
num = num;

function minus(e) {
  if (num !==0) {
    num = num--;
    return num
  }
}


export default App;
