import logo from './logo.svg';
import './App.css';


function App() {
  return (
    <div className="App">
      <h1 className="number" ref='num'>0</h1>
      <button type="button" className="plus_btn" onClick={handleClick}>+1</button>
    </div>
  );
}

function handleClick() {
  let num = parseInt(this.refs.num.innerHtml);
  console.log(num);
}


export default App;
