import React, {useState} from 'react';
import './App.css';
import {database} from './firebase';

function App() {
  return (
    <div className="App">
      <h1 className="number">{number}</h1>
      <div className="buttons">
        <button type="button" className="plus_btn" onClick={plus}>+1</button>
        {/* <button type="button" className="minus_btn" onClick={minus}>-1</button> */}
      </div>  
    </div>
  );
}

let number = 0;

function plus() {
  number++;
  console.log(number);
}

function writeCurrentNum(num) {
  database().ref('currentNum' + num).set({
    currentNum: num
  });
}


export default App;
