import React, { useState } from "react";
import { WriteUser, UpdateUser } from './Firebase';
import './App.css';

const Counter = () => {

    // getter변수(num), setter함수(setNum)를 포함한 useState선언
    const [num, setNum] = useState(0);
    
    // 증가하는 함수
    const plus = () => {
        setNum(num + 1);
        UpdateUser(num + 1); // 현재 수 데이터베이스에 쓰기
    }
    
    // 감소하는 함수
    const minus = () => {
        if(num !== 0){
            setNum(num - 1);
            UpdateUser(num - 1);
        }
    }
    
    // user 정보 저장하는 함수
    const userInfo = (name, phone, num) => {
        WriteUser(name, phone, num);
    }

    return (
        <div className="App">
            <input type ="text" placeholder="이름" name="name" /><br/>
            <input type ="text" placeholder="휴대폰 번호" name="phone" />
            <h1>{num}</h1>
            <button type="button" className="plus_btn" onClick={plus}>+1</button>
            <button type="button" className="minus_btn" onClick={minus}>-1</button><br/><br/>
            <button type="submit" className="submit_btn" onClick={userInfo}>제출</button>
        </div>
    );
}

export default Counter;
