import React, { useState } from "react";
import { WriteUser } from './Firebase';
import './App.css';

const User =() => {

    // name, phone useState선언
    state = {
        name: '',
        phone: ''
    }

    // input 함수
    const handleChange = (e) => {
        
        console.log(this);
        this.setState({
            [e.target.name]: e.target.value
        });

        // let target = e.target.name;
        // let value = e.target.value;

        // if (target === "name") {
        //     setInputs(value);
        // }else if (target === "phone") {
        //     const regex = /^[0-9\b -]{0,13}$/; //길이는 13까지, 숫자와 -만 허용
        //     if (regex.test(value)) {
        //         setInputs(value);
        //     }
        // }
    }

    // submit 함수 
    const handleSubmit = (e) => {
        e.preventDefault(); // 새로고침 안 되도록
        const name = e.inputs.name;
        const phone = e.inputs.phone;
        console.log("이름 :" + name )
        console.log("번호 :" + phone )
        // WriteUser(name, phone, 0); // 파이어베이스에 저장
    }

    return (
        <div className="App">
            <form>
                    <p>정보 입력</p>
                    <p><input type ="text" placeholder="이름을 입력해주세요." name="name" value={name} onChange={handleChange}/><br/>
                    <input type ="text" placeholder="010-0000-0000" name="phone" value={phone} onChange={handleChange}/><br/>
                    <button type="submit" className="submit_btn" onClick={handleSubmit}>제출</button></p>
            </form>
        </div>
    );
}

export default User;
