import logo from './logo.svg';
import './App.css';

function plus(e) {
  const h1 = document.querySelector(".number");
  let number = parseInt(h1.innerText); 

  number++;
  console.log(number);
}

h1.innerText = number;

function App() {
  return (
    <div className="App">
      <h1 className="number">0</h1>
      <button type="button" className="plus_btn" onClick={plus}>+1</button>
    </div>
  );
}

export default App;
