import React, {useState} from "react";
import {database} from './Firebase';
import './App.css';

const Counter = () => {
    // getter변수(num), setter함수(setNum)를 포함한 useState선언
    const [num, setNum] = useState(0);

    // 증가하는 함수
    const plus = () => {
        setNum(num + 1);
        console.log(num);
        // writeCurrentNum(num); // 현재 수 데이터베이스에 쓰기
    }

    // 감소하는 함수
    function minus() {
    if(num !== 0){
        setNum(num - 1);
        console.log(num);
        // writeCurrentNum(num);
    }
    }

    // 데이터베이스에 쓰기
    function writeCurrentNum(num) {
    database().ref('currentNum').set({
        currentNum: num
    });
    }

    return (
        <div>
            <h1>0</h1>
            <button type="button" className="plus_btn" onClick={plus}>+1</button>
            <button type="button" className="minus_btn" onClick={minus}>-1</button>
        </div>
    );
}

export default Counter;
