import React, {useState} from 'react';
import './App.css';

function App() {
  return (
    <div className="App">
      <h1 className="number">{number}</h1>
      <div className="buttons">
        <button type="button" className="plus_btn" onClick={this.plus()}>+1</button>
        <button type="button" className="minus_btn" onClick={this.minus()}>-1</button>
      </div>  
    </div>
  );
}

let number = 0;
// var database = firebase.database();

function plus(e) {
  number++;
  console.log(number);
}

function writeCurrentNum(num) {
  firebase.database().ref('currentNum' + num).set({
    currentNum: num
  });
}


export default App;
