import React, { useState } from "react";
import { WriteUser } from './Firebase';
import './App.css';

const User =() => {

    // name, phone useState선언
    const [name, setName] = useState ('');
    const [phone, setPhone] = useState ('');


    // 이름 저장 함수
    const changeName = (e) => {
        setName(e.target.value);
        // }else if (target === "phone") {
        //     const regex = /^[0-9\b -]{0,13}$/; //길이는 13까지, 숫자와 -만 허용
        //     if (regex.test(value)) {
        //         setInputs ({phone: value});
        //     }
        // }
    }

    // 폰번호 저장 함수
    const changePhone = (e) => {
        const regex = /^[0-9\b -]{0,13}$/; //길이는 13까지, 숫자와 -만 허용
        if (regex.test(e.target.value)) {
            setPhone(e.target.value);
        }
    }    

    // 제출버튼 비활성화
    const [disabled, setDisabled] = useState(false);

    // submit 함수 
    const handleSubmit = (e) => {
        e.preventDefault(); // 새로고침 안 되도록
        WriteUser(name, phone, 0); // 파이어베이스에 저장
        setDisabled(true);
    }

    return (
        <div className="App">
            <form>
                    <p>정보 입력</p>
                    <p><input type ="text" placeholder="이름을 입력해주세요." name="name" value={name} onChange={changeName}/><br/>
                    <input type ="text" placeholder="휴대폰번호를 입력해주세요." name="phone" value={phone} onChange={changePhone}/><br/>
                    <button type="submit" className="submit_btn" onClick={handleSubmit} disabled={disabled}>제출</button></p>
            </form>
        </div>
    );
}

export default User;
