import React, { useState } from "react";
import { CreateUser, ReadUser} from './Firebase';
import './App.css';

const User =() => {
    
    // name, phone useState선언
    const [name, setName] = useState ('');
    const [phone, setPhone] = useState ('');
    
    
    // 이름 저장 함수
    const changeName = (e) => {
        setName(e.target.value);
    }
    
    // 폰번호 저장 함수
    const changePhone = (e) => {
        setPhone(e.target.value);
    }    
    
    // db와 같은 회원정보는 제출버튼 비활성화
    const [disabled, setDisabled] = useState(false);
    
    // submit 함수 
    const handleSubmit = (e) => {
        e.preventDefault(); // 새로고침 안 되도록
        CreateUser(name, phone); // 파이어베이스에 저장
        }
        
        return e (
            <div className="App">
            {/* <form onSubmit={handleSubmit}>
                    <p>정보 입력</p>
                    <p><input type ="text" placeholder="이름을 입력해주세요." name="name" value={name} onChange={changeName}/><br/>
                    <input type ="text" placeholder="휴대폰번호를 입력해주세요." name="phone" value={phone} onChange={changePhone}/><br/>
                    <button type="submit" className="submit_btn" disabled={disabled}>제출</button></p>
                </form> */}
        </div>
    );
}

const domContainer = 
document.querySelector('#submit');
document.querySelector('#name');
document.querySelector('#phone');

ReactDOM.render(e(handleSubmit), domContainer);

export default User;
