import React from 'react';
import './App.css';
import {database} from './firebase';

function App() {
  return (
    <div className="App">
      <h1 className="number">{num}</h1>
      <div className="buttons">
        <button type="button" className="plus_btn" onClick={plus}>+1</button>
        <button type="button" className="minus_btn" onClick={minus}>-1</button>
      </div>  
    </div>
  );
}

let num = 0;

// 1 증가
function plus() {
  num+=1;
  // console.log(num);
  // writeCurrentNum(num);
}

// 1 감소
function minus() {
  if(num !== 0){
    num--;
    // console.log(num);
    // writeCurrentNum(num);
  }
}

// 데이터베이스에 쓰기
function writeCurrentNum(num) {
  database().ref('currentNum').set({
    currentNum: num
  });
}


export default App;
