import React, { useState } from "react";
import { UpdateNum, UpdateNumLogin } from './Firebase';
import './App.css';
import './script/script';

const Counter = () => {
    return (
        <div className="btn_area">
            <div className="btn_wr">
                <button type="button" onClick={Plus}>click</button>
            </div>
            {/* <div class="result">COUNT: 0</div>  */}
        </div>
    );
}

 // 증가하는 함수
const Plus = (userId, currentNum) => {
    const [num, setNum] = useState(0);
    //num useState선언
    
    if(!currentNum) {
        setNum(num+1);
        UpdateNum(num + 1); // 현재 수 데이터베이스에 쓰기
    }else{
        setNum(currentNum+1);
        UpdateNumLogin(userId, (currentNum+1));
    }    
}

export {Counter, Plus};
