import React, { useState } from "react";
import { WriteUser, UpdateUser } from './Firebase';

const User =() => {

    // input 함수
    const onChange = (e) => {
        let target = e.target.name;
        let value = e.target.value;

        if (target === "name") {
            value === name;
            setName(name);
        }else if (target === "phone") {
            value === phone;
            const regex = /^[0-9\b -]{0,13}$/; //길이는 13까지, 숫자와 -만 허용
            if (regex.test(phone)) {
                setPhone(phone);
            }
        }
    }

    // submit 함수 
    const handleSubmit = (e, name, phone) => {
        e.preventDefault(); // 새로고침 안 되도록
        WriteUser(name, phone, 0); // 파이어베이스에 저장
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                    <p>정보 입력</p>
                    <p><input type ="text" placeholder="이름을 입력해주세요." value={name}  onChange={onChange}/><br/>
                    <input type ="text" placeholder="010-0000-0000" value={phone} onChange={onChange}/><br/>
                    <button type="submit" className="submit_btn">제출</button></p>
            </form>
        </div>
    );
}
