import React from 'react';
import './App.css';
import { firestore } from "./firebase";


function App() {
  return (
    <div className="App">
      <h1 className="number">0</h1>
      <div className="buttons">
        <button type="button" className="plus_btn" onClick={plus}>+1</button>
        <button type="button" className="minus_btn" onClick={minus}>-1</button>
      </div>  
    </div>
  );
}


function plus(e) {
  const h1 = document.querySelector(".number");
  let num = parseInt(h1.innerHTML);
  num++; 
  h1.innerHTML = num;
}

function minus(e) {
  const h1 = document.querySelector(".number");
  let num = parseInt(h1.innerHTML);
  if (num !==0) {
    num--;
  }
  h1.innerHTML = num;
}


export default App;
