import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <div className="content">
        <h1 className="number">0</h1>
        <button type="button" className="plus_btn">+1</button>
      </div>
    </div>
  );
}

const h1 = document.querySelector(".number");
const btn = document.querySelector(".plus_btn");

let number = parseInt(h1.innerHTML);

btn.addEventListener("click", plus);

function plus(e) {
  number++;
  console.log(number);
}

export default App;
