import React, { useState } from "react";
import { WriteCurrentNum, UpdateNum} from './Firebase';
import './App.css';

const Counter = () => {
    // getter변수(num), setter함수(setNum)를 포함한 useState선언
    const [num, setNum] = useState(0);
    WriteCurrentNum(num);

    // 증가하는 함수
    const plus = () => {
        setNum(num + 1);
        UpdateNum(num + 1); // 현재 수 데이터베이스에 쓰기
    }

    // 감소하는 함수
    const minus = () => {
        if(num !== 0){
            setNum(num - 1);
            UpdateNum(num - 1);
        }
    }
}

export default Counter;
